fn parse_string(challenge_str: &str) -> (i32, i32) {
    let mut floor_num: i32 = 0;
    let mut first_entered_basement_index: i32 = -1;
    
    for (index, character) in challenge_str.chars().enumerate() {
        if floor_num == -1 && first_entered_basement_index == -1 { // When Santa enters the basement (-1) for the first time, the index will be -1
            first_entered_basement_index = index as i32;
        }
        
       floor_num += match character {
            '(' => 1,
            ')' => -1,
            _ => 0
        };
    }
    
    return (floor_num, first_entered_basement_index);
}