fn parse_string(challenge_str: &str) -> i32 {
    let mut floor_num: i32 = 0;
    
    for (index, character) in challenge_str.chars().enumerate() {
        if character == '('{
            floor_num += 1;
        } else if character == ')'{
            floor_num -=1 ;   
        }
    }
    
    return floor_num;
}